import React from 'react';
import "./BlogBody.css"


function BlogBody(){
    function handlePost(){
        alert("You Clicked the Post button!");
        const dummyPost = {title: "Sample Title",author:"Myself"};
        alert("Created a dummy post : "+  dummyPost.title);
        if (dummyPost.title){
            console.log("The d")
        }
    }
    return (
        <main>
            <h2>Create a New Blog Post</h2>
            <form>
                <label>
                    Title:
                    <input type = "text" name = "title" placeholder="Title of my awesome post" />
                </label>
                <br /><br />
                <p>This information is Confidential.</p>
                <label>
                    Body:
                    <textarea name="body" placeholder="Write your blog content here..."></textarea>
                </label>
                <br /><br />
                <button className= "blogbutton"type = "button" onClick={handlePost}>Post</button>
            </form>
        </main>
    );
}

export default BlogBody;
