import React, { useState } from 'react';
import "./BlogBody.css";
import EmailForm from './EmailForm.js';

function BlogBody() {
  const [statusMessage, setStatusMessage] = useState('');
  const [title, setTitle] = useState('');
  const [posts, setPosts] = useState([]);
  const [body, setBody] = useState('');
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');
  const [email, setEmail] = useState('');
  const [showBlogInterface, setShowBlogInterface] = useState(false);
  const [userProfile, setUserProfile] = useState(null);
  const [userBlogs, setUserBlogs] = useState([]);

  function handleDeletePost(indexToRemove) {
    const confirmed = window.confirm("Are you sure you want to delete this post?");
    if (!confirmed) return;

    const updatedPosts = posts.filter((_, index) => index !== indexToRemove);
    setPosts(updatedPosts);
  }

  const handleAccessBlogs = (email, blogList, profileData) => {
    console.log('User email:', email);
    console.log('Blog list:', blogList);
    console.log('Profile data:', profileData);

    setEmail(email);
    setUserBlogs(blogList);
    setUserProfile(profileData);
    setShowBlogInterface(true);

    if (blogList && Array.isArray(blogList.posts)) {
      setPosts(blogList.posts);
    } else if (Array.isArray(blogList)) {
      setPosts(blogList);
    } else {
      setPosts([]);
    }
  };

  function handleTitleChange(event) {
    setTitle(event.target.value);
  }

  function handleBodyChange(event) {
    setBody(event.target.value);
  }

  async function handlePost() {
    if (title.trim() === '') {
      alert('Please enter a title');
      return;
    }

    if (body.trim() === '') {
      alert('Please enter the body');
      return;
    }

    const newPost = {
      title,
      body,
      date: date || new Date().toLocaleDateString(),
      time: time || new Date().toLocaleTimeString()
    };

    try {
      console.log('Sending blog post to backend:', {
        title: newPost.title,
        body: newPost.body,
        email: email
      });

      const response = await fetch('http://localhost:5000/api/blogs', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          title: newPost.title,
          body: newPost.body,
          email: email
        }),
      });

      const data = await response.json();
      console.log('Backend response:', data);

      if (response.ok && (data.success || data.blog)) {
        setStatusMessage(`✅ Blog "${newPost.title}" saved!`);
        setPosts([
          ...posts,
          {
            title: data.blog?.title || newPost.title,
            body: data.blog?.content || newPost.body,
            date: new Date(data.blog?.created_at || Date.now()).toLocaleDateString(),
            time: new Date(data.blog?.created_at || Date.now()).toLocaleTimeString()
          }
        ]);
      } else {
        setStatusMessage('⚠️ Blog post failed to save.');
      }

      setTitle('');
      setBody('');
      setDate('');
      setTime('');
    } catch (error) {
      console.error('Error posting blog:', error);
      setStatusMessage('❌ Error communicating with backend.');
    } finally {
      setTimeout(() => setStatusMessage(''), 5000);
    }
  }

  return (
    <main>
      {!showBlogInterface ? (
        <EmailForm headerText="Welcome to Blog Manager" onAccessBlogs={handleAccessBlogs} />
      ) : (
        <div>
          {/* User Profile Information */}
          {userProfile && (
            <div className='user-profile-info'>
              <h3>Welcome, {userProfile.user}!</h3>
              <p>Role: {userProfile.role}</p>
              <p>Email: {email}</p>
            </div>
          )}

          {/* Blog Post Creation Form */}
          <h2>Create a New Blog Post</h2>
          <p style={{ color: statusMessage.startsWith("✅") ? "green" : "red" }}>
            {statusMessage}
          </p>

          <form>
            <label>
              Title:
              <input
                type="text"
                value={title}
                onChange={handleTitleChange}
                placeholder="Title of my awesome post"
              />
            </label>
            <br /><br />

            <p>This information is Confidential.</p>
            <input
              type="date"
              value={date}
              onChange={(e) => setDate(e.target.value)}
            />
            <input
              type="time"
              value={time}
              onChange={(e) => setTime(e.target.value)}
            />
            <br /><br />

            <label>
              Body:
              <textarea
                name="body"
                value={body}
                onChange={handleBodyChange}
                placeholder="Write your blog content here..."
              ></textarea>
            </label>
            <br /><br />

            <div className="button-group">
              <button className="blogbutton" type="button" onClick={handlePost}>
                Post
              </button>
            </div>
          </form>

          <hr />
          <div className='blog-posts-container'>
            <h3>Posted Titles:</h3>
            {posts.length === 0 ? (
              <p>No posts yet. Start typing!</p>
            ) : (
              posts.map((post, index) => (
                <div key={index} className='blog-post'>
                  <div className='post-header'>
                    <h3 className='post-title'>{post.title}</h3>
                  </div>
                  

                  <h4>{post.title}</h4>
                  <p>{post.body}</p>
                  <p>Posted at {post.date}, {post.time}</p>
                  <button onClick={() => handleDeletePost(index)}>Delete</button>
                </div>
              ))
            )}
          </div>
        </div>
      )}
    </main>
  );
}

export default BlogBody;
