import React, { useState, useEffect } from "react";

function Profile() {
  const [username, setUsername] = useState('');
  const [age, setAge] = useState('');

  useEffect(() => {
    console.log('Username changed:', username);
    console.log('Age changed to', age);
  }, [username, age]);

  return (
    <div style={{ padding: '1rem' }}>
      <h2>Profile</h2>
      <label>
        Username:
        <input
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          style={{ width: '50%' }}
        />
        Age:
        <input
          value={age}
          onChange={(e) => setAge(e.target.value)}
          style={{ width: '50%' }}
        />
      </label>
      <p>Welcome {username} and you are {age} years old!</p>
    </div>
  );
}

function FetchDemoAsync() {
  const [profile, setProfile] = useState(null);

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await fetch('http://localhost:3001/api/profile');
        const data = await response.json();
        console.log("Fetched data (async/await):", data);
        setProfile(data.Profile); // ðŸŽ¯ Access the 'Profile' object inside the JSON
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    }

    fetchData();
  }, []);

  return (
    <div style={{ padding: '1rem' }}>
      <h2>Fetch Demo - async/await</h2>
      <p>Check the console to see data from /api/profile.</p>
      {profile ? (
        <div>
          <p>User: {profile.name}</p>
          <p>Role: {profile.role}</p>
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
}

// âœ… One default + one named export
export default Profile;
export { FetchDemoAsync };
