import React, { useState, useEffect } from "react";

function BlogPosts() {
  const [blogCount, setBlogCount] = useState(0); 
  const [blogs, setBlogs] = useState([]);

  useEffect(() => {
    console.log("Blog count updated:", blogCount);
  }, [blogCount]);

  const addBlogPost = () => {
    const newPost = {
      title: `Blog Post #${blogCount + 1}`,
      body: `This is the content for blog post #${blogCount + 1}`
    };
    setBlogs([...blogs, newPost]);
    setBlogCount(blogCount + 1);
  };

  return (
    <>
      <div style={{ padding: "1rem" }}>
        <h2>Blog Posts</h2>
        {blogs.length === 0 ? (
          <p>No blog posts found.</p>
        ) : (
          <ul>
            {blogs.map((blog, index) => (
              <li key={index} className="blog-item">
                <h3>{blog.title}</h3>
                <p>{blog.body}</p>
              </li>
            ))}
          </ul>
        )}
        <p>Number of blog posts: {blogCount}</p>
      </div>
      <button onClick={addBlogPost}>ADD</button>
    </>
  );
}

export default BlogPosts;
