import React, { useState } from 'react';
import "./BlogBody.css";

function BlogBody() {
  const [title, setTitle] = useState('');
  const [posts,setPosts] = useState([]);

  function handleTitleChange(event) {
    setTitle(event.target.value);
    console.log("Current title:", event.target.value); //
  }

  function handlePost() {
    alert("You Clicked the Post button!");
    const newPost = { title: title };
    console.log("New Post Object:", newPost);
    alert("Name of your title: " + title);
    setPosts([...posts,newPost]);
  }
  if(title==''){
    alert('Please enter a title');
    return;
  }
  function handleTest() {
    alert("You clicked on Test");
  }

  return (
    <main>
      <h2>Create a New Blog Post</h2>
      <form>
        <label>
          Title:
          <input
            type="text"
            value={title}
            onChange={handleTitleChange}
            placeholder="Title of my awesome post"
          />
        </label>
        <br /><br />
        <p>This information is Confidential.</p>
        <label>
          Body:
          <textarea name="body" placeholder="Write your blog content here..."></textarea>
        </label>
        <br /><br />
        <div className="button-group">
          <button className="blogbutton" type="button" onClick={handlePost}>Post</button>
          <button className="blogbutton" type="button" onClick={handleTest}>Test</button>
        </div>
      </form>
      {posts.map((post,index)=>(
        <div key = {index}>
            <h4>{post.title}</h4>
        </div>
      ))}
    </main>
  );
}

export default BlogBody;
