import React, { useState } from 'react';
import "./BlogBody.css";

function BlogBody() {
  const [title, setTitle] = useState('');
  const [posts, setPosts] = useState([]);
  const [body, setBody] = useState('');
  const [date,setDate] = useState('');
  const[time,setTime] = useState('')
  const currentDateTime = new Date().toLocaleString();
  
  
  function handleDeletePost(indexToRemove){
    const confirmed = window
    
    const updatedPosts = posts.filter((_,index) => index !== indexToRemove);
    setPosts(updatedPosts);
  } 

  function handleTitleChange(event) {
    setTitle(event.target.value);
    console.log("Current title:", event.target.value);
  }
  function handleBodyChange(event){
    setBody(event.target.value);
    console.log("current blog:" , event.target.value);
  }
  function handlePost() {
    if (title === '') {
      alert('Please enter a title');
      return;
    }

    alert("You Clicked the Post button!");
    const newPost = { title: title , body:body, date:date ,time:time };
    console.log("New Post Object:", newPost);

    alert("Name of your title: " + title);
    setPosts([...posts, newPost]);
    setTitle(''); 
    setBody('');
    setDate('');
    setTime('');
  }



  return (
    <main>
      <h2>Create a New Blog Post</h2>
      <form>
        <label>
          Title:
          <input
            type="text"
            value={title}
            onChange={handleTitleChange}
            placeholder="Title of my awesome post"
          />
        </label>
        <br /><br />
        <p>This information is Confidential.</p>
        <input type ="date" value={date} onChange={(e) => setDate(e.target.value)}/>
        <input type="time" value={time} onChange={(e)=> setTime(e.target.value)}/>
        <label>
          Body:
          <textarea name="body" value={body} onChange={handleBodyChange} placeholder="Write your blog content here..."></textarea>
        </label>
        <br /><br />
        <div className="button-group">
          <button className="blogbutton" type="button" onClick={handlePost}>Post</button>
        </div>
      </form>

      <hr />
      <h3>Posted Titles:</h3>
      {posts.length === 0 ? (
        <p>No posts yet. Start typing!</p>
      ) : (
        posts.map((post, index) => (
          <div key={index}>
            <h4>{post.title}</h4>
            <p>{post.body}</p>
            <p>Blog Posted at {currentDateTime}</p>
            <button onClick={() => handleDeletePost(index)}>Delete</button>

          </div>
        ))
      )}
    </main>
  );
}

export default BlogBody;
