import React, { useState } from 'react';
import "./BlogBody.css";

function BlogBody() {
  const [statusMessage, setStatusMessage] = useState('');
  const [title, setTitle] = useState('');
  const [posts, setPosts] = useState([]);
  const [body, setBody] = useState('');
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');

  function handleDeletePost(indexToRemove) {
    const confirmed = window.confirm("Are you sure you want to delete this post?");
    if (!confirmed) return;

    const updatedPosts = posts.filter((_, index) => index !== indexToRemove);
    setPosts(updatedPosts);
  }

  function handleTitleChange(event) {
    setTitle(event.target.value);
  }

  function handleBodyChange(event) {
    setBody(event.target.value);
  }

  async function handlePost() {
    if (title.trim() === '') {
      alert('Please enter a title');
      return;
    }

    const postPayload = {
      title,
      body
    };

    try {
      const response = await fetch('http://localhost:5000/api/blog-post', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(postPayload)
      });

      const data = await response.json();
      console.log('Backend response:', data);

      if (response.ok && data.blog) {
        setStatusMessage(`✅ Blog "${data.blog.title}" saved!`);
        setPosts([...posts, {
          title: data.blog.title,
          body: data.blog.content,
          date: new Date(data.blog.created_at).toLocaleDateString(),
          time: new Date(data.blog.created_at).toLocaleTimeString()
        }]);
      } else {
        setStatusMessage('⚠️ Blog post failed to save.');
      }

      setTitle('');
      setBody('');
      setDate('');
      setTime('');
    } catch (error) {
      console.error('Error posting blog:', error);
      setStatusMessage('❌ Error communicating with backend.');
    } finally {
      setTimeout(() => setStatusMessage(''), 5000);
    }
  }

  return (
    <main>
      <h2>Create a New Blog Post</h2>
      <p style={{ color: statusMessage.startsWith("✅") ? "green" : "red" }}>{statusMessage}</p>

      <form>
        <label>
          Title:
          <input
            type="text"
            value={title}
            onChange={handleTitleChange}
            placeholder="Title of my awesome post"
          />
        </label>
        <br /><br />

        <p>This information is Confidential.</p>
        <input type="date" value={date} onChange={(e) => setDate(e.target.value)} />
        <input type="time" value={time} onChange={(e) => setTime(e.target.value)} />
        <br /><br />

        <label>
          Body:
          <textarea
            name="body"
            value={body}
            onChange={handleBodyChange}
            placeholder="Write your blog content here..."
          ></textarea>
        </label>
        <br /><br />

        <div className="button-group">
          <button className="blogbutton" type="button" onClick={handlePost}>Post</button>
        </div>
      </form>

      <hr />
      <h3>Posted Titles:</h3>
      {posts.length === 0 ? (
        <p>No posts yet. Start typing!</p>
      ) : (
        posts.map((post, index) => (
          <div key={index}>
            <h4>{post.title}</h4>
            <p>{post.body}</p>
            <p>Posted at {post.date}, {post.time}</p>
            <button onClick={() => handleDeletePost(index)}>Delete</button>
          </div>
        ))
      )}
    </main>
  );
}

export default BlogBody;
