import React, { useState, useEffect } from "react";

function BlogPosts() {
  const [blogCount, setBlogCount] = useState(0); 
  useEffect(() => {
    console.log("Blog count updated:", blogCount);
  }, [blogCount]);

  return (
    <>
      <div style={{ padding: "1rem" }}>
        <h2>Blog Posts</h2>
        {BlogPosts.length === 0?(
            <p>No blog posts found.</p>
        ):(
            <ul>
                {BlogPosts.map((blog,index))}
            </ul>
        )}
        <p>Number of blog posts: {blogCount}</p>
      </div>
      <button onClick={() => setBlogCount(blogCount + 1)}>ADD</button>
    </>
  );
}

export default BlogPosts;
